import { Toast } from "mint-ui";
export default {
  data: function data() {
    return {
      uname: "",
      upwd: ""
    };
  },
  methods: {
    reg: function reg() {
      var _this = this;

      var uname = this.uname;
      var upwd = this.upwd;
      var postdata = this.qs.stringify({
        uname: uname,
        upwd: upwd
      });
      var url = "http://127.0.0.1:3000/user/register";
      this.axios.post(url, postdata).then(function (res) {
        console.log(res);

        if (res.data.code == 1) {
          _this.$router.push("/login");
        } else if (res.data.code == -1) {
          Toast("注册失败");
        } else {
          Toast("用户名已被占用");
        }
      });
    }
  }
};
