import { Toast } from "mint-ui";
export default {
  data: function data() {
    return {
      uname: "",
      upwd: ""
    };
  },
  methods: {
    butLogin: function butLogin() {
      var _this = this;

      var uname = this.uname;
      var upwd = this.upwd;
      var reg = /^[a-z0-9]{3,12}$/i;

      if (!reg.test(uname)) {
        Toast("用户名格式不正确，请检查");
        return;
      }

      var url = "http://127.0.0.1:3000/user/login?uname=" + uname + "&upwd=" + upwd;
      this.axios.get(url).then(function (res) {
        if (res.data.code == 1) {
          _this.$router.push("/Home");
        } else {
          Toast("用户名或密码错误");
        }
      });
    }
  }
};
