import swipre from "../sub/swiper.vue";
import { Toast } from "mint-ui";
export default {
  data: function data() {
    return {
      rows: [],
      info: {},
      pid: this.$route.query.pid,
      val: 1
    };
  },
  methods: {
    addCart: function addCart() {
      var pid = this.pid;
      var price = this.info.price;
      var uid = 1;
      var url = "http://127.0.0.1:3000/user/addCart?pid=" + pid + "&price=" + price + "&uid=" + uid;
      this.axios.get(url).then(function (res) {
        if (res.data.data == 401) {
          Toast("请登录");
          return;
        }

        if (res.data.code == 1) {
          Toast("添加成功");
        } else {
          Toast("添加失败,请登录");
        }
      });
    },
    getImages: function getImages() {
      var _this = this;

      var url = "http://127.0.0.1:3000/user/imglist";
      this.axios.get(url).then(function (res) {
        _this.rows = res.data;
      });
    },
    findGoodInfo: function findGoodInfo() {
      var _this2 = this;

      var url = "http://127.0.0.1:3000/user/findProduct?pid=" + this.pid;
      this.axios.get(url).then(function (res) {
        _this2.info = res.data[0];
      });
    },
    goodAdd: function goodAdd() {
      this.val++;
    },
    goodSub: function goodSub() {
      if (this.val > 1) {
        this.val--;
      }
    }
  },
  created: function created() {
    this.getImages();
    this.findGoodInfo();
  },
  components: {
    "swipre-box": swipre
  }
};
